import React, { Component } from 'react';
import {
  AppRegistry,
  StyleSheet,
  Text,
  TextInput,
  View,
  TabBarIOS,
  NavigatorIOS
} from 'react-native';

var base64Icon =
  'data:image/png;base64,';

class Phinancials extends Component {

  state = {
    selectedTab: 'home'
  }

  _renderWelcomePage() {
    return (
      <View style={styles.container}>
        <Text style={styles.welcome}>
          Welcome to Phinancials!
        </Text>
        <Text style={styles.instructions}>
          The easy-as-hell financials application.
        </Text>
        <Text style={styles.instructions}>
          Tap "New form" for a new expenses form.
        </Text>
      </View>
    );
  }

  _renderNewForm() {
    return (
      <View style={styles.container}>
        <Text style={styles.welcome}>
          New expenses form:
        </Text>
        <View>
          <TextInput placeholder="Add expenses..." style={styles.textInput} />
        </View>
      </View>
    );
  }

  render() {
    return (
      <TabBarIOS>
        <TabBarIOS.Item
            title="Home"
            selected={this.state.selectedTab === 'home'}
            icon={{uri: base64Icon, scale: 3}}
            onPress={() => {
              this.setState({
                selectedTab: 'home',
              });
            }}>
          {this._renderWelcomePage()}
        </TabBarIOS.Item>
        <TabBarIOS.Item
            title="New Form"
            selected={this.state.selectedTab === 'newForm'}
            icon={{uri: base64Icon, scale: 3}}
            onPress={() => {
              this.setState({
                selectedTab: 'newForm',
              });
            }}>
          {this._renderNewForm()}
        </TabBarIOS.Item>
      </TabBarIOS>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5FCFF',
  },
  welcome: {
    fontSize: 20,
    textAlign: 'center',
    margin: 10,
  },
  instructions: {
    textAlign: 'center',
    color: '#333333',
    marginBottom: 5,
  },
  textInput: {
    height: 40,
    width:200,
    borderColor: 'grey',
    backgroundColor: 'white',
    borderWidth: 1
  }
});

AppRegistry.registerComponent('Phinancials', () => Phinancials);
